SELECT ATunesCustomers.CustomerID as 'Customer ID', ATunesCustomers.FirstName, ATunesCustomers.LastName,  CustomerAccounts.CurrentCredit as Credit, Purchases.DateOfPurchase as 'Date of Purchase', Purchases.SongID as 'Song ID' FROM ATunesCustomers LEFT JOIN Purchases ON (ATunesCustomers.CustomerID = Purchases.CustomerID) LEFT JOIN CustomerAccounts ON (ATunesCustomers.CustomerID = CustomerAccounts.CustomerID) ORDER BY ATunesCustomers.CustomerID;